		if ($this->isColumnModified(StatusesPeer::STA_MESSAGE)) $criteria->add(StatusesPeer::STA_MESSAGE, $this->sta_message);
		if ($this->isColumnModified(StatusesPeer::STA_UPDATED_TIME)) $criteria->add(StatusesPeer::STA_UPDATED_TIME, $this->sta_updated_time);
		if ($this->isColumnModified(StatusesPeer::STA_LIKE_COUNT)) $criteria->add(StatusesPeer::STA_LIKE_COUNT, $this->sta_like_count);
		if ($this->isColumnModified(StatusesPeer::STA_COMMENTS_COUNT)) $criteria->add(StatusesPeer::STA_COMMENTS_COUNT, $this->sta_comments_count);
		if ($this->isColumnModified(StatusesPeer::CREATED_AT)) $criteria->add(StatusesPeer::CREATED_AT, $this->created_at);
